(function() {
    'use strict';

    angular
      .module('otusjs.labelMaker.labelBuilder.labelComponents')
      .component('defaultLabel', {
          transclude: true,
          template:'<div ng-show="$ctrl.componentLabel == \'participant\' && $ctrl.type == \'barcode\'" class="default-label"><div class="default-label-text"><h3>{{$ctrl.baseInfo.participant_name}}</h3><span>CQ:{{$ctrl.baseInfo.cq_group}}</span><br></div><svg id="participantbarcode" class="barcode"></svg></div><div ng-show="$ctrl.componentLabel == \'participant\' && $ctrl.type == \'qrcode\'" layout="row" layout-align="space-between center" class="default-label"><div><div class="default-label-text"><h3>{{$ctrl.baseInfo.participant_name}}</h3><span>CQ:{{$ctrl.baseInfo.cq_group}}</span><br></div></div><div><div id="participantqr" class="qrcode"></div></div></div><div ng-show="$ctrl.componentLabel == \'biomaterial\' && $ctrl.type == \'barcode\'" class="default-label"><p>{{$ctrl.biomaterial.label}}</p><div ng-show="$ctrl.identified" ng-style=\'{"overflow": "hidden","white-space": "nowrap","text-overflow": "ellipsis"}\'><span>{{$ctrl.baseInfo.participant_name}}</span><br></div><div ng-show="$ctrl.identified" layout-align="space-between center" layout="row" flex><span>NR:{{$ctrl.baseInfo.recruitment_number}}</span> <span>Sexo:{{$ctrl.baseInfo.gender}}</span></div><div ng-show="$ctrl.identified" layout-align="space-between center" layout="row" flex><span>DN:{{$ctrl.baseInfo.birthday}}</span> <span ng-show="$ctrl.baseInfo.laboratoryIdentification != null">Kit: {{$ctrl.baseInfo.laboratoryIdentification}}</span></div><svg id="biomaterialbarcode" class="barcode"></svg></div><div ng-show="$ctrl.componentLabel == \'biomaterial\' && $ctrl.type == \'qrcode\'" layout="row" layout-align="space-between center" class="default-label"><div><p>{{$ctrl.biomaterial.label}}</p><div ng-show="$ctrl.identified" ng-style=\'{"overflow": "hidden","white-space": "nowrap","text-overflow": "ellipsis"}\'><span>{{$ctrl.baseInfo.participant_name}}</span><br></div><div ng-show="$ctrl.identified" layout-align="space-between center" layout="row" flex><span>NR:{{$ctrl.baseInfo.recruitment_number}}</span> <span>Sexo:{{$ctrl.baseInfo.gender}}</span></div><div ng-show="$ctrl.identified" layout-align="space-between center" layout="row" flex><span>DN:{{$ctrl.baseInfo.birthday}}</span> <span ng-show="$ctrl.baseInfo.laboratoryIdentification != null">Kit: {{$ctrl.baseInfo.laboratoryIdentification}}</span></div></div><div><div id="biomaterialqr" class="qrcode"></div></div></div><div ng-show="$ctrl.componentLabel == \'unattached\'" class="default-label"><div class="default-label-text"><p>Laboratório</p><span>Centro:{{$ctrl.baseInfo.laboratoryFieldCenter}}</span><br><span>CQ:{{$ctrl.baseInfo.cq_group}}</span><br></div><svg ng-show="$ctrl.type == \'barcode\'" id="unattachedbarcode" class="barcode"></svg><div ng-show="$ctrl.type == \'qrcode\'" id="unattachedqr" class="qrcode"></div></div>',
          controller: Controller,
          bindings: {
            componentLabel: "<",
            base: '<',
            biomaterial: '<'
          }
      });

      Controller.$inject = [
        '$scope',
        '$element',
        '$compile',
        'BARCODE_DEFAULT_SETTINGS'
      ];

      function Controller($scope, $element, $compile, BARCODE_SETTINGS) {
        var self = this;

        self.renderBarcode = renderBarcode;
        self.baseInfo = angular.copy(self.base);
        self.identified = self.baseInfo.printStructure.identified.value;
        self.type = self.baseInfo.printStructure.type.value;

        self.$onInit = function() {
          $compile($element.contents())($scope);
          if(self.biomaterial.hasOwnProperty('aliquotCode')){
            self.biomaterial.code = self.biomaterial.aliquotCode
          }
          if(self.type == 'qrcode') {
            renderQrcode();
          }else if(self.type == 'barcode') {
            renderBarcode();
          }
        };

        function renderBarcode() {
          if(self.componentLabel == 'participant') {
            var barcodeContainer = $element.find('#participantbarcode')[0];
            JsBarcode(barcodeContainer, self.baseInfo.recruitment_number, BARCODE_SETTINGS);
          }else if(self.componentLabel == 'biomaterial') {
            var barcodeContainer = $element.find('#biomaterialbarcode')[0];
            JsBarcode(barcodeContainer, self.biomaterial.code, BARCODE_SETTINGS)
          }else if(self.componentLabel == 'unattached') {
            var barcodeContainer = $element.find('#unattachedbarcode')[0];
            JsBarcode(barcodeContainer, self.baseInfo.laboratoryIdentification, BARCODE_SETTINGS)
          }
        }

        function renderQrcode() {
          var typeNumber = 2;
          var errorCorrectionLevel = 'L';
          var qr = qrcode(typeNumber, errorCorrectionLevel);

          if(self.componentLabel == 'participant'){
            addQrIntoElement(qr, "#participantqr", self.baseInfo.recruitment_number.toString())
          }else if(self.componentLabel == 'biomaterial') {
            addQrIntoElement(qr, "#biomaterialqr", self.biomaterial.code)
          }else if(self.componentLabel == 'unattached') {
            addQrIntoElement(qr, '#unattachedqr', self.baseInfo.laboratoryIdentification)
          }
        }

        function addQrIntoElement(qr, elementId, datastring){
          var qrcodeContainer = $element.find(elementId)[0]
          qr.addData(datastring);
          qr.make();
          qrcodeContainer.innerHTML = qr.createImgTag()
        }
      }
}());

